  item: [{ categoryPk: number; categoryName: string; categoryCode: string }];
  currntTab: number;
  setCurrentTab: React.Dispatch<React.SetStateAction<number>>;
}
const CategoryTab: React.FC<CategorieProps> = ({
  item,
