import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styled from "styled-components";
import axios from "axios";
import PostDetail from "../components/PostDetail";

const Post = () => {
  let { post_pk } = useParams();
  const [postDetail, setPostDetail] = useState<any>([]);
  //console.log(post_pk, "pk");
  function timeForToday(value: string) {
    // console.log(value);
    const today = new Date();
    const timeValue = new Date(value);

    const betweenTime = Math.floor(
      (today.getTime() - timeValue.getTime()) / 1000 / 60
    );
    if (betweenTime < 1) return "방금전";
    if (betweenTime < 60) {
      return `${betweenTime}분전`;
    }

    const betweenTimeHour = Math.floor(betweenTime / 60);
    if (betweenTimeHour < 24) {
      return `${betweenTimeHour}시간전`;
    } else {
      const overDate = value.split("T")[0];
      return overDate.slice(2, 10);
    }
  }

  const axios_GetPost_Detail = async () => {
    await axios
      .get("../../../data/Post.json")
      .then((el) => {
        //console.log(el.data.POSTS, "post");
        const data = el.data.POSTS.filter(
          (el: any) => el.pk === Number(post_pk)
        );
        ///console.log(data, "???");
        setPostDetail(data);
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    axios_GetPost_Detail();
  }, []);

  console.log(postDetail, "detail");
  return (
    <Container>
      <Nav>
        <img
          className="arrow"
          src={"../../../arrow-left.svg"}
          alt="back-arrow"
        ></img>
        <div className="arrow-explain">글 목록으로</div>
      </Nav>
      <PostDetail postDetail={postDetail} />
    </Container>
  );
};
export default Post;
const Container = styled.div`
  display: flex;
  position: relative;
  flex-direction: column;
  border: solid grey 1px;
  border-radius: 10px;
  width: 360px;
  height: 821px;
`;
const Nav = styled.div`
  display: flex;
  flex-direction: row;
  .arrow {
    width: 18px;
    height: 18px;
    margin: 21px 11px 10px 21px;
    cursor: pointer;
  }
  .arrow-explain {
    margin-top: 21px;
    font-size: 14px;
    font-weight: 700;
    line-height: 15px;
    color: #b4b4b4;
  }
`;
const Box = styled.div`
  display: flex;
  flex-direction: column;
  border: 0 solid black;
  width: 100%;
`;
const PostData = styled.article`
  margin: 0;
  padding: 14px 14px 14px 16px;
`;

const Header = styled.header`
  display: flex;
  > img {
    width: 32px;
    height: 32px;
    display: block;
    border-radius: 50%;
  }

  .user_name {
    display: flex;
    height: 12px;
    font-weight: 700;
    font-size: 12px;
    line-height: 18px;
    color: #222222;
  }
  .category {
    color: #b4b4b4;
    font-weight: 500;
    font-size: 12px;
    line-height: 18px;
  }
`;

const Main = styled.div`
  display: flex;
  flex-direction: column;

  .title {
    font-size: 16px;
    font-weight: 700;
    line-height: 25px;
    color: #222222;
    margin-bottom: 6px;
    cursor: pointer;
  }
  .content {
    font-size: 14px;
    font-weight: 400;
    line-height: 25px;
    color: #7a7a7a;
    cursor: pointer;
  }
  .content_image {
    height: 160px;
    cursor: pointer;
  }
`;
