import React, { useState, useEffect } from "react";
import styled from "styled-components";
interface DetailProps {
  postDetail: [
    {
      categoryPk: number;
      categoryName: string;
      pk: number;
      title: string;
      content: string;
      viewCount: number;
      likeCount: number;
      commentCount: number;
      imageUrl: null | string;
      writtenAt: string;
      writerNickName: string;
      writerProfileUrl: string;
    }
  ];
  init: number;
}

const PostDetail: React.FC<DetailProps> = ({ postDetail, init }) => {
  function timeForToday(value: string) {
    const today = new Date();
    const timeValue = new Date(value);

    const betweenTime = Math.floor(
      (today.getTime() - timeValue.getTime()) / 1000 / 60
    );
    if (betweenTime < 1) return "방금전";
    if (betweenTime < 60) {
      return `${betweenTime}분전`;
    }

    const betweenTimeHour = Math.floor(betweenTime / 60);
    if (betweenTimeHour < 24) {
      return `${betweenTimeHour}시간전`;
    } else {
      const overDate = value.split("T")[0];
      return overDate.slice(2, 10);
    }
  }
  function textLengthOverCut(txt: string, len: any, lastTxt: string) {
    if (len == "" || len == null) {
      // 기본값
      len = 20;
    }
    if (lastTxt == "" || lastTxt == null) {
      // 기본값
      lastTxt = "...";
    }
    if (txt.length > len) {
      txt = txt.substring(0, len) + lastTxt;
    }
    return txt;
  }

  return (
    <Container>
      {init ? (
        <PostData>
          <Header>
            <img
              src={postDetail[0].writerProfileUrl}
              alt={postDetail[0].writerNickName}
            />
            <div>
              <div className="user_name">{postDetail[0].writerNickName}</div>
              <div className="category">
                {postDetail[0].categoryName}
                {timeForToday(postDetail[0].writtenAt)}
              </div>
            </div>
          </Header>
          <Main>
            <div className="title">{postDetail[0].title}</div>
            <div className="content">{postDetail[0].content}</div>
            {!postDetail[0].imageUrl ? (
              <></>
            ) : (
              <img className="content_image" src={postDetail[0].imageUrl}></img>
            )}
          </Main>
        </PostData>
      ) : (
        ""
      )}
    </Container>
  );
};

export default PostDetail;
const Container = styled.div`
  display: flex;
  flex-direction: column;
  border: 0 solid black;
  width: 100%;
`;
const PostData = styled.article`
  margin: 0;
  padding: 14px 14px 14px 16px;
`;

const Header = styled.header`
  display: flex;

  > img {
    width: 32px;
    height: 32px;
    display: block;
    border-radius: 50%;
  }

  .user_name {
    display: flex;
    height: 12px;
    font-weight: 700;
    font-size: 12px;
    line-height: 18px;
    color: #222222;
  }
  .category {
    color: #b4b4b4;
    font-weight: 500;
    font-size: 12px;
    line-height: 18px;
  }
`;

const Main = styled.div`
  display: flex;
  flex-direction: column;

  .title {
    font-size: 16px;
    font-weight: 700;
    line-height: 25px;
    color: #222222;
    margin-bottom: 6px;
    cursor: pointer;
  }
  .content {
    font-size: 14px;
    font-weight: 400;
    line-height: 25px;
    color: #7a7a7a;
    cursor: pointer;
  }
  .content_image {
    cursor: pointer;
  }
`;
