import styled from "styled-components";
import * as React from "react";
import { useState } from "react";

interface CategorieProps {
  item: { categoryPk: number; categoryName: string; categoryCode: string };
  index: number;
}
const CategoryTab: React.FC<CategorieProps> = ({ item, index }) => {
  const [currentTab, setCurrntTab] = useState(0);

  const handleBtnClick = (index: number) => {
    setCurrntTab(index - 1);
  };
  console.log(currentTab, "tav");
  console.log(index, "index");
  return (
    <ButtonGroup>
      <Button
        onClick={() => handleBtnClick(item.categoryPk)}
        className={index === currentTab ? "focused" : ""}
      >
        {item.categoryName}
      </Button>
    </ButtonGroup>
  );
};
const ButtonGroup = styled.div``;
const Button = styled.button`
  margin-left: 2px;
  overflow: hidden;
  background: #ffffff;
  padding: 12px 16px;
  text-align: center;
  border: 1px solid #e8e8e8;
  border-radius: 20px;
  font-weight: 500;
  font-size: 14px;
  line-height: 14px;
  height: 100%;
  float: left;
  cursor: pointer;
  &:active {
    color: white;
    background: #2c7fff;
  }
  .focused {
    background-color: #00cc99;
    color: rgba(255, 255, 255, 1);
    transition: 0.3s;
  }
`;

export default CategoryTab;
