import styled from "styled-components";
import * as React from "react";
import { useState } from "react";

interface CategorieProps {
  item: [{ categoryPk: number; categoryName: string; categoryCode: string }];
}
const CategoryTab: React.FC<CategorieProps> = ({ item }) => {
  const [currentTab, setCurrntTab] = useState(0);

  const handleBtnClick = (index: number) => {
    setCurrntTab(index);
  };
  console.log(item);
  //console.log(index, "index");
  return (
    <ButtonGroup>
      {item.map((el, index) => {
        return (
          <li
            key={index}
            className={currentTab === index ? "submenu focused" : "submenu"}
            onClick={() => handleBtnClick(index)}
          ></li>
        );
      })}
      {/* <Button onClick={() => handleBtnClick(item.categoryPk)}>
        {item.categoryName}
      </Button> */}
    </ButtonGroup>
  );
};
const ButtonGroup = styled.div``;
const Button = styled.ul`
  margin-left: 2px;
  background: #ffffff;
  padding: 12px 16px;
  text-align: center;
  border: 1px solid #e8e8e8;
  border-radius: 20px;
  font-weight: 500;
  font-size: 14px;
  line-height: 14px;
  height: 100%;
  float: left;
  cursor: pointer;
  .submenu {
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    width: 100%;
    padding: 1rem;
    cursor: pointer;
    box-shadow: 0px -3px 10px 0px rgba(0, 0, 0, 0.2);
  }

  .focused {
    background-color: #00cc99;
    color: rgba(255, 255, 255, 1);
    transition: 0.3s;
  }
`;

export default CategoryTab;
